<?php $reqlevel = 3;include("membersonly.inc.php");$cdt=date('Y-m-d');$edtm=date('Y-m-d h:i:s a');set_time_limit(0);$main_scat=0;$main_product=0;$get2=mysqli_query($conn,"select * from hsn where sl>0") or die(mysqli_error($conn));while($row2=mysqli_fetch_array($get2)){$old=$row2['old'];$new=$row2['new'];$get1=mysqli_query($conn,"select * from main_scat WHERE hsn='$old'") or die(mysqli_error($conn));if(mysqli_num_rows($get1)>0){mysqli_query($conn,"UPDATE main_scat SET hsn='$new' WHERE hsn='$old'");$main_scat++;}$get3=mysqli_query($conn,"select * from main_product WHERE hsn='$old'") or die(mysqli_error($conn));if(mysqli_num_rows($get3)>0){mysqli_query($conn,"UPDATE main_product SET hsn='$new' WHERE hsn='$old'");$main_product++;}}echo "Total main_scat : ".$main_scat;echo "<br>";echo "Total main_product : ".$main_product;?>